mapper.insert:
INSERT INTO student (
	student_id
	, student_pw
	, student_name
) VALUES (
	#{studentId}
	, PASSWORD(#{studentPw})
	, #{studentName}
)

mapper.select:
SELECT student_no studentNo , student_id studentId , student_name studentName FROM student
student_name like CONCAT('%', #{searchWord}, '%')
GROUP BY student_no DESC LIMIT #{beginRow}, #{rowPerPage}

mapper.insert:
