mapper.insert:
INSERT INTO employee (
	emp_id
	, emp_pw
	, emp_name
) VALUES (
	#{empId}
	, PASSWORD(#{empPw})
	, #{empName}
)

mapper.select:
SELECT emp_no empNo , emp_id empId , emp_name empName FROM employee
emp_name like CONCAT('%', #{searchWord}, '%')
GROUP BY emp_no DESC LIMIT #{beginRow}, #{rowPerPage}
