mapper.insert:
INSERT INTO teacher (
	teacher_id
	, teacher_pw
	, teacher_name
) VALUES (
	#{teacherId}
	, PASSWORD(#{teacherPw})
	, #{teacherName}
)

mapper.select:
SELECT teacher_no teacherNo , teacher_id teacherId , teacher_name teacherName FROM teacher
teacher_name like CONCAT('%', #{searchWord}, '%')
GROUP BY teacher_no DESC LIMIT #{beginRow}, #{rowPerPage}
