mapper.update:
UPDATE question
SET question_title = #{questionTitle}
WHERE question_idx = #{questionIdx}

mapper.insert:
INSERT INTO test(
	test_title
	, test_date
	) VALUE (
		#{testTitle}
		, CURDATE()
	)

mapper.select:
SELECT student_no studentNo , student_id studentId , student_name studentName FROM student
student_name like CONCAT('%', #{searchWord}, '%')
GROUP BY student_no DESC LIMIT #{beginRow}, #{rowPerPage}
